	<script>
	var telaMobile = false;

	function setMobile() {
		document.body.classList.add('mobile', 'mobile-travado');
		try {
			document.querySelector('#lp-meta').content = "width=360, user-scalable=yes";
		} catch (err) {
			var newMetaElement = document.createElement('meta');

			newMetaElement.name = 'viewport';
			newMetaElement.content = 'width=360, user-scalable=yes';
			newMetaElement.id = 'lp-meta';

			var headElement = document.head || document.getElementsByTagName('head')[0];

			var existingMetaElement = document.getElementById('lp-meta');
			if (existingMetaElement) {
				headElement.removeChild(existingMetaElement);
			}
			headElement.appendChild(newMetaElement);

		}
	}

	function setDesktop() {
		document.body.classList.remove('mobile', 'mobile-travado', 'mobile-desk');
		document.querySelector('#lp-meta').content = "width=device-width, initial-scale=1.0";

		telaMobile = false;

	}

	// troca de view mobile/desktop quando faz o resize
	function checkWidth(forcarMobile = false) {
		if (forcarMobile) {
			setMobile();
		}
		var width = window.innerWidth;

		if (width >= 1024) {
			setDesktop();
		} else {
			setMobile();

			telaMobile = true;

		}
	}

	checkWidth();

	// Nome ruim, mas pega os js dos sliders
	var teste_arr = [];
</script>
