<script id="_content_js">
	
	var apenasMobile = 1 || 0;

	if (apenasMobile === 1) {
		setMobile();

		document.body.classList.add('mobile-apenasmob')

	} else {
		window.addEventListener('resize', () => checkWidth());
	}

	var mobileNaoTravado = false;

	if (window.innerWidth > 1024 && apenasMobile === 1) {
		document.body.classList.add('mobile', 'mobile-desk');
		document.body.classList.remove('mobile-travado');
		mobileNaoTravado = true;
	}

	// se caso o mobile não seja travado mas esteja no mobile ele irá forçar os sliders a ter 1 visualização
	if (window.innerWidth < 1024) {
		apenasMobile = 1;
		mobileNaoTravado = false;
		setMobile();
	}
</script>
