<script>
	// força os sliders a ficarem com apenas 1 visualização caso o mobile esteja travado
	window.addEventListener('load', () => {
		if (teste_arr) {
			teste_arr?.forEach(slider => {
				if (apenasMobile === 1) {
					slider.glide.update({
						perView: 1,
					});
				} else {
					// mesmo salvando o perview errado no mobile ele irá colocar o correto aqui
					slider.glide.update({
						perView: slider.perView,
					});
				}
			});
		}
	});
</script>	<!--	0.021584987640381	-->
